#define _USE_MATH_DEFINES#include <iostream>#include <cmath>using namespace std;struct diem{	float x;	float y;};typedef struct diem DIEM;struct duongtron{	DIEM I;	float R;};typedef struct duongtron DUONGTRON;void Nhap(DUONGTRON &);void Nhap(DUONGTRON &a){	cout << "Nhap tam: ";	cin >> a.I.x >> a.I.y;	cout << endl;	cout << "Nhap ban kinh: ";	cin >> a.R;}int main(){	DUONGTRON a;	Nhap(a);	cout << "Chu vi duong tron la: " << 2 * M_PI * a.R;;	return 0;}