#include <iostream>#include <cmath>using namespace std;struct diem{	float x;	float y;};typedef struct diem DIEM;struct duongtron{	DIEM I;	float R;};typedef struct duongtron DUONGTRON;void Nhap(DUONGTRON&);int TuongDoi(DUONGTRON, DUONGTRON);float KhoangCach(DUONGTRON, DUONGTRON);void Nhap(DUONGTRON& a){	cout << "Nhap tam: ";	cin >> a.I.x >> a.I.y;	cout << endl;	cout << "Nhap ban kinh: ";	cin >> a.R;}float KhoangCach(DUONGTRON a, DUONGTRON b){	return sqrt((a.I.x - b.I.x) * (a.I.x - b.I.x) + (a.I.y - b.I.y) * (a.I.y - b.I.y));}int TuongDoi(DUONGTRON c1, DUONGTRON c2){	float kc = KhoangCach(c1, c2);	if (kc == 0 && c1.R == c2.R)		return 0;	if (kc > (c1.R + c2.R))		return 1;	if (kc == (c1.R + c2.R))		return 2;	if (kc < (c1.R + c2.R) && kc > abs(c1.R - c2.R))		return 3;	if (kc == abs(c1.R - c2.R))		return 4;	return 5;}int main(){	DUONGTRON a,b;	Nhap(a);	Nhap(b);	int t = TuongDoi(a, b);	if (t == 0)		cout << "Trung nhau";	if (t == 1)		cout << "Roi nhau";	if (t == 2)		cout<<"Tiep xuc ngoai";	if (t == 3)		cout << "Cat nhau";	if (t == 4)		cout << "Tiep xuc trong";	if (t == 5)		cout << "Chua trong nhau";	return 0;}